import React, { Component } from 'react';
class Layout extends Component {
	state = {};
	handleClick = () => {
		console.log('hello');
	};
	render() {
		return (
			<div>
				<h1>Let's flip a coin</h1>
				<img
					src="https://bjc.edc.org/June2017/bjc-r/img/5-algorithms/img_flipping-a-coin/Heads.png"
					alt="head"
				/>
				<br />
				<button onClick={this.handleClick}>>Flip Me! </button>/button>
				<h2>Out of 2 flips, there have been 1 heads and 1 tails</h2>
			</div>
		);
	}
}

export default Layout;
