import React, { Component } from 'react';
class Layout extends Component {
	static defaultProps = {
		image: [
			{
				side: 'head',
				imgSrc: 'https://bjc.edc.org/June2017/bjc-r/img/5-algorithms/img_flipping-a-coin/Heads.png'
			},
			{
				side: 'tail',
				imgSrc: 'https://bjc.edc.org/June2017/bjc-r/img/5-algorithms/img_flipping-a-coin/Tails.png'
			}
		]
	};

	state = {
		img: null,
		flip: 1,
		head: 0,
		tail: 0
	};
	handleClick = () => {
		console.log('hello');
	};
	render() {
		return (
			<div>
				<h1>Let's flip a coin</h1>
				<img src={this.state.img} alt="coins" />
				<br />
				<button onClick={this.handleClick}>Flip Me! </button>
				<h2>
					Out of {this.state.flip} flips, there have been {this.state.head} heads and {this.state.tails} tails
				</h2>
			</div>
		);
	}
}

export default Layout;
